import * as SucMonch from './components';
import olData from './utils/olData';
import './styles/main.scss'

function plugin(Vue) {
    if (plugin.installed) {
        return;
    }
    plugin.installed = true;

    Object.keys(SucMonch).forEach(key => {
        if(key!=='OlEchartsLayer') {
            Vue.component(key, SucMonch[key]);
        }
    });
}

const API = {
    version: process.env.VERSION,
    install: plugin
}

export * from "./components"
export * from "./utils/helpers"
export * from "./utils/proj"
export * from "./plugins/plot/gispace"
export {
    olData
}
export default API;
