import Vue from 'vue';
import {Extent} from "ol/extent";

export interface Point {
    id: string,
    lon: number,
    lat: number
}

export interface FitDataInterface {
    center: number[],  // 坐标，坐标系和组件的projection属性一致
    zoom: number   // 缩放层级
}

export declare class OlHeatMap extends Vue {
    /**
     * 图层透明度
     * @default 1
     */
    opacity?: number;
    /**
     * 热力图颜色梯度
     * @default ['#00f', '#0ff', '#0f0', '#ff0', '#f00']
     */
    gradient?: string[];
    /**
     * 半径尺寸，单位像素
     * @default 8
     */
    radius?: number;
    /**
     * 模糊尺寸，单位像素
     * @default 15
     */
    blur?: number;
    /**
     * 点位
     * @default []
     */
    points: Array<Point>;
    /**
     * 图层是否在初始化时自适应屏幕
     * @default false
     */
    isFit?: boolean;
    /**
     * 图层id 可通过 layer.get('id') 获取
     */
    layerId?: string;
    /**
     * 图层层级
     * @default 2
     */
    zIndex?: number;
    /**
     * 输入输出数据采用的坐标系
     * @default EPSG:4326
     */
    projection?: string;
    className?: string;
    visible?: boolean;
    extent?: Extent;
    minResolution?: number;
    maxResolution?: number;
    minZoom?: number;
    maxZoom?: number;

    /**
     * 图层是否在初始化时自适应屏幕后触发
     * @param e 当前视图中心坐标与缩放层级
     */
    $emit(eventName: 'fit', e: FitDataInterface): this;
}
