import Vue, { VNode } from 'vue';
import { StyleLike } from './style';

export interface MarkerOptions {
    id: string,
    lat: number,
    lon: number,
    projection?: string,
    name?: string,
    info?: any,
    overLabel?: string,
    style?: StyleLike
}

export type SelectedPoint = MarkerOptions&{coord:number[]};

export declare class OlMarker extends Vue {
    /**
     * 点位配置项
     */
    options: MarkerOptions;
    /**
     * 点击空白处是否设置 selectPoint=null，进而关闭相关 overlay
     * @default true
     */
    blankClear?: boolean;

    /**
     * 鼠标悬浮在点位上时触发
     */
    $emit(eventName: 'pointermove'):this;
    /**
     * 鼠标点击在点位上时触发
     */
    $emit(eventName: 'singleclick'):this;

    /**
     * 作用域插槽
     */
    $scopedSlots: {
        'default': (props: { selectPoint: SelectedPoint|null }) => VNode[]
    };
}
