import Vue from 'vue'
import { Style } from './style';

export interface DataSourceItem {
    speed?: number;
    from: {
        city: string,
        lnglat: number[]
    },
    to: {
        city: string,
        lnglat: number[]
    }
}

export interface ArrowStyle {
    img: string;
    anchor?: number[];
    imgSize: number[];
}

export declare class OlMigrate extends Vue {
    /**
     * 数据源
     */
    dataSource: DataSourceItem[];
    /**
     * 线样式
     * @default {
                        stroke: {
                            color: '#12b5d0',
                            width: 2
                        }
                    }
     */
    lineStyle?: Style;
    /**
     * 文字颜色 若不设置，则采用线条颜色
     */
    textColor?: string|number[];
    /**
     * 箭头类型 arrowType 为 "image" 时，必须同时设置 arrowStyle
     * @default default
     */
    arrowType?: 'default'|'image';
    /**
     * 路径分段最小长度，同时也是箭头默认样式时构成箭头的原点的半径长度
     * @default 3
     */
    moveRadius?: number;
    /**
     * 默认箭头类型的颜色 arrowType 为 "default" 时起作用
     * @default white
     */
    moveColor?: string;
    /**
     * 图片箭头的样式 arrowType 为 "image" 时起作用
     */
    arrowStyle?: ArrowStyle;
    /**
     * 是否显示文字
     * @default true
     */
    showText?: boolean;
    /**
     * 飞线速度
     * @default normal
     */
    speed?: 'slow'|'normal';
    /**
     * 图层层级
     * @default 0
     */
    zIndex?: number;
    /**
     * 输入输出数据采用的坐标系
     * @default EPSG:4326
     */
    projection?: string;
}
