import { StyleLike } from './style';
import type Feature from 'ol/Feature'
import {Coordinate} from "ol/coordinate";
import {VectorLayer} from "./vectorLayer";

export type GeomType =
    'Polygon'
    | 'MultiPolygon'
    | 'LineString'
    | 'MultiLineString'
    | 'Point'
    | 'MultiPoint'
    | 'Circle'
    | 'Sector';
export type CoordinateType = Coordinate|Coordinate[]|Coordinate[][]|Coordinate[][][];

export interface PathOptions {
    id: string,
    type: GeomType,
    coords: CoordinateType,
    radius?: number,
    style?: StyleLike
}

export declare class OlPath extends VectorLayer {
    /**
     * 矢量配置项
     */
    options: PathOptions;
    /**
     * 图层层级
     * @default 0
     */
    zIndex?: number;
    /**
     * 输入输出数据采用的坐标系
     * @default EPSG:4326
     */
    projection?: string;

    /**
     * 矢量第一次创建时触发
     */
    $emit(eventName: 'create-end', feature: Feature):this;
}
