import Vue from 'vue'
import type { Coordinate } from 'ol/coordinate';
import type { Extent } from 'ol/extent';
import type { ProjectionLike } from 'ol/proj';
import type Layer from 'ol/layer/Layer';
import type Feature from 'ol/Feature'
import type MapBrowserEvent from 'ol/MapBrowserEvent'

export interface MapOptions {
    id?: string,  // 地图id
    events?: string[], // 地图事件监听
    pixelRatio?: number
}

export interface ViewCenter {
    coord: Coordinate,  // 坐标
    projection: string  // 坐标所属的坐标系
}

export interface MapEventInterface {
    coords: number[],
    projection: string,
    event: MapBrowserEvent,
    layer: Layer,
    feature: Feature
}

export interface MapBlankEventInterface {
    coords: number[],
    projection: string
}

export declare class SucMap extends Vue {
    options?: MapOptions;
    /**
     * 视图中心坐标
     * @default {
                    'coord': [120, 30],
                    'projection': 'EPSG:4326'
                }
    */
    center?: ViewCenter;
    constrainRotation?: boolean | number;
    enableRotation?: boolean;
    extent?: Extent;
    constrainOnlyCenter?: boolean;
    smoothExtentConstraint?: boolean;
    maxResolution?: number;
    minResolution?: number;
    maxZoom?: number;
    minZoom?: number;
    multiWorld?: boolean;
    constrainResolution?: boolean;
    smoothResolutionConstraint?: boolean;
    showFullExtent?: boolean;
    projection?: ProjectionLike;
    resolution?: number;
    resolutions?: number[];
    rotation?: number;
    zoom?: number;
    zoomFactor?: number;
    /**
     * 鼠标悬浮在矢量图层时触发
     */
    $emit(eventName: 'pointermove', e: MapEventInterface): this;
    /**
     * 鼠标点击矢量图层时触发
     */
    $emit(eventName: 'singleclick', e: MapEventInterface): this;
    /**
     * 鼠标悬浮在矢量图层时触发
     */
    $emit(eventName: 'pointermove-blank', e: MapBlankEventInterface): this;
    /**
     * 鼠标点击矢量图层时触发
     */
    $emit(eventName: 'singleclick-blank', e: MapBlankEventInterface): this;
}
