import { VueRouter } from 'vue-router/types/router';
import { LoadingBar, SucConfirm, SucMessage, SucNotice } from './components';
import * as components from './components';
import { Loading } from './element';

const sucsoft: any = {
    ...components
};

const install: any = function (Vue: any, opts: any = {}) {
    if (install.installed) return;
    Object.keys(sucsoft).forEach((key: string) => {
        Vue.component(key, sucsoft[key]);
    });
};

if (typeof window !== 'undefined' && (<any>window).Vue) {
    install((<any>window).Vue);
}

const API = {
    version: process.env.VERSION,
    install,
};

export const version = process.env.VERSION;
export * from './components';
export default API;
